%producto de TODOS los elementos de un arreglo de duales function local = prod_elements(x)  xD0 = x.f0;  xD1 = x.f1;    x0 = xD0(:);  k0 = find(mynot(x0));  lgz = length (k0);  if (lgz >= 2)    local = dual1(0, 0);  elseif (lgz == 1)    x1 = xD1(:);        NOx0 = mynot(x0);    newx0 = x0 + NOx0;        f0p = prod(newx0,1);    f1 = f0p.*x1(k0);        local = dual1(0, f1);  else    x1 = xD1(:);    f0 = prod (x0,1);    auxsum = sum(x1./x0);    f1 = f0.*auxsum;    local = dual1(f0, f1);  endend%function local = mynot(A)        Ai=imag(A);    Ar=real(A);    local = ~Ai .* ~Ar;end    